  @supports (-moz-user-select: none) {
    :root {
      --svg-status-bg: var(--bg-1);
    }
  }
  body {
    font-family: var(--ui-font) !important;
  }
  body,
  body.navless {
    color: var(--fg-1) !important;
    background-color: var(--bg-0) !important;
  }
  body a,
  body a:hover {
    color: var(--ac);
  }
  body hr {
    border-color: var(--bg-3);
  }
  body h1,
  body .h1,
  body h2,
  body .h2,
  body h3,
  body .h3,
  body h4,
  body .h4,
  body h5,
  body .h5,
  body h6,
  body .h6 {
    color: var(--fg-1);
  }
  body time {
    color: inherit;
  }
  body :not(pre) > code {
    color: var(--fg-1);
    background-color: var(--bg-2);
    box-shadow: inset 0 0 0 1px var(--bg-5);
  }
  body pre {
    color: var(--fg-1);
    border-color: var(--bg-5);
    background-color: var(--bg-2);
  }
  body pre[class *= 'description'] {
    border-left-width: 4px;
    font-size: 0.875rem;
    color: var(--fg-4) !important;
    border-color: var(--bg-5) !important;
  }
  body > pre:not([class]) {
    background-color: transparent !important;
  }
  body kbd {
    border-radius: 3px;
    color: var(--fg-1) !important;
    background-color: var(--bg-2) !important;
    box-shadow: inset 0 0 0 1px var(--bg-5), inset 0 -3px var(--bg-4) !important;
  }
  body .gl-link,
  body .gl-link:hover {
    color: var(--ac);
  }
  body .author-link,
  .branch-commit .commit-sha {
    color: var(--ac);
  }
  body .text-monospace,
  body .code.diff-table,
  body pre,
  body code,
  body kbd,
  body samp,
  body .monospace,
  body .ref-name,
  body .commit-sha,
  body .pipeline-number,
  body .label-branch,
  body .git-revision-dropdown .dropdown-content ul li a,
  body table.code,
  body [class *= 'git-commit'] {
    font-family: var(--mono-font) !important;
  }
  body .container {
    border-color: var(--bg-5) !important;
  }
  body .danger-title,
  body .gl-field-error,
  body .invalid-feedback {
    color: var(--v1-6) !important;
  }
  body [class^='price-'] {
    color: var(--v6-7) !important;
  }
  body .warning-title {
    color: var(--v2-6) !important;
  }
  body .legend-success,
  body .valid-feedback {
    color: var(--v4-6) !important;
  }
  body .account-well {
    color: var(--fg-1) !important;
    border-color: var(--bg-5) !important;
    background-color: var(--bg-2) !important;
  }
  body [class *= 'two-factor-auth'] svg {
    background-color: var(--inverse) !important;
    box-shadow: 0 0 0 4px var(--inverse);
  }
  body .progress {
    background-color: var(--bg-5) !important;
  }
  body .progress-bar.bg-primary {
    background-color: var(--v0-9) !important;
  }
  body .progress.repository-languages-bar {
    background-color: var(--bg-3) !important;
  }
  body .progress.repository-languages-bar .progress-bar {
    margin-right: unset;
  }
  body .progress.repository-languages-bar .progress-bar:last-child:nth-child(-n+4) {
    flex-grow: 1;
  }
  body .progress [style*='#375eab'] {
    background-color: #00add8 !important;
  }
  body .progress [style*='#776791'] {
    background-color: #000100 !important;
  }
  body .text-info:not(#z) {
    color: var(--v6-6) !important;
  }
  body .text-info:not(#z) > svg {
